import React, { useState, lazy } from "react";
import "./explanations.css";
import useSound from "use-sound";

export default function Stocks({ onClose }) {
  //const fs = require("fs");

  const subTopics = [
    "Advantages and Disadvantages",
    "Common and Preferred Stocks",
    "How do you buy stocks",
    "Shareholder and Equity Ownership",
    "Understanding Stocks",
  ];

  //fs.readdir("./audios/stocks", function (err, files) {
  //  //handling error
  //  if (err) {
  //    return console.log("Unable to scan directory: " + err);
  //  }
  //  //listing all files using forEach
  //  files.forEach(function (file) {
  //    // Do whatever you want to do with the file
  //    console.log(file);
  //  });
  //});

  const CreateBtns = (subTopic) => {
    const DynamicAudio = require(`./audios/stocks/${subTopic}.mp3`);

    console.log(DynamicAudio);
    const [audioPlaying, setAudioPlaying] = useState(false);

    const [DynAudio, { stop }] = useSound(DynamicAudio, {
      volume: 1.0,
    });

    const play = () => {
      if (!audioPlaying) {
        setAudioPlaying(true);
        DynAudio();
      } else {
        setAudioPlaying(false);
        stop();
      }
    };

    return (
      <button
        className="button buttonExplanation"
        //TODO: Add reasonable keys
        key={Math.random()}
        onClick={play}
        expl={subTopic}
      >
        {subTopic}
      </button>
    );
  };

  var explanationButtons = subTopics.map(CreateBtns);

  //const [isPlaying, setIsPlaying] = useState(false);
  //let audio = new Audio("/audio/stocks/Understanding Stocks.mp3");

  //const start = () => {
  //  //if (!isPlaying) {
  //  //  audio.play();
  //  //  setIsPlaying(true);
  //  //} else {
  //  //  audio.pause();
  //  //  audio.currentTime = 0;
  //  //  setIsPlaying(false);
  //  //}
  //  const [play] = useSound(audio);
  //};

  return (
    <>
      <div className="modalStyles" />
      <div className="overlayStyles">
        Stocks
        <div>{explanationButtons}</div>
        <button className="button buttonEndExplanation" onClick={onClose}>
          End Explanation
        </button>
      </div>
    </>
  );
}
