import { useMemo, useState, useEffect, useRef } from "react";
import ExplanationButtons from "./ExplanationButtons";
import sttFromMic from "./S2t";

const EducatorHome = () => {
  const [recording, setRecording] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [explanations, setExplanations] = useState([]);
  //const isFirstRun = useRef(true);

  const handleCallback = (apiData) => {
    if (apiData !== "none") {
      setApiResponse(apiData);
    }
  };

  // useEffect(() => {
  //   if (isFirstRun.current) {
  //     isFirstRun.current = false;
  //   } else if (!explanations.includes(apiResponse)) {
  //     setExplanations([...explanations, apiResponse]);
  //   }
  //   //eslint-disable-next-line
  // }, [apiResponse]);

  useEffect(() => {
    if (!explanations.includes(apiResponse)) {
      setExplanations([...explanations, apiResponse]);
    }
    //eslint-disable-next-line
  }, [apiResponse]);

  let [startRec, stopRec] = useMemo(() => {
    return sttFromMic(handleCallback);
  }, []);

  switch (recording) {
    case false:
      return (
        <button
          className="button buttonStart"
          onClick={() => {
            startRec();
            setRecording(true);
          }}
        >
          Start Educator
        </button>
      );
    case true:
      return (
        <>
          <button
            className="button buttonStop"
            onClick={() => {
              stopRec();
              setRecording(false);
            }}
          >
            Stop Educator
          </button>
          {apiResponse && <ExplanationButtons topics={explanations} />}
        </>
      );
  }
};

export default EducatorHome;
