import { useState, useEffect } from "react";
import sttFromMic from "./S2t";

const EducatorButton = () => {
  const [recording, setRecording] = useState(false);

  const startFunc = () => {
    setRecording(true);
    sttFromMic();
  };

  switch (recording) {
    case false:
      return (
        <button className="button button1" onClick={sttFromMic}>
          Start Educator
        </button>
      );
    case true:
      return (
        <button className="button button1" onClick={sttFromMic}>
          Stop Educator
        </button>
      );
  }
};

export default EducatorButton;
