import { useState } from "react";
import sttFromMic from "./S2t";

const EducatorButton = () => {
  const [recording, setRecording] = useState(false);

  const startFunc = () => {
    setRecording(true);
    recursiveStt();
  };

  const stopFunc = () => {
    setRecording(false);
  };

  async function recursiveStt() {
    var flag = await sttFromMic();
    if (flag && recording) {
      recursiveStt();
    }
  }

  switch (recording) {
    case false:
      return (
        <button className="button button1" onClick={startFunc}>
          Start Educator
        </button>
      );
    case true:
      return (
        <button className="button button1" onClick={stopFunc}>
          Stop Educator
        </button>
      );
  }
};

export default EducatorButton;
