import { useState } from "react";
import sttFromMic from "./S2t";

const EducatorButton = () => {
  const [recording, setRecording] = useState(false);

  const clickFunc = () => {
    setRecording(true);
    sttFromMic();
  };

  return (
    !recording && (
      <button className="button button1" onClick={clickFunc}>
        Start Educator
      </button>
    )
  );
};

export default EducatorButton;
