import React, { useEffect, useRef } from "react";
import "./explanations.css";
import PlayVideo from "./PlayVideo";
import { Text } from "react-native";

const Risk = ({ onClose }) => {
  const videosList = [
    "How to gain Diversification Benefits",
    "Portfolio Diversification",
    "Sharpe Ratio",
    "Understanding Risks",
  ];

  const vidRef = useRef();

  var explanationButtons = videosList.map((name) => (
    <button
      className="button buttonExplanation"
      key={Math.random()}
      onClick={() => <PlayVideo videoStr={`clips/${name}.mp4`} />}
      expl={name}
    >
      <Text style={{ color: "white" }}>{name.replace(" ", "\n")}</Text>
    </button>
  ));

  return (
    <>
      <div className="modalStyles" />
      <div className="overlayStyles">
        {explanationButtons}
        <button className="button buttonEndExplanation" onClick={onClose}>
          End Explanation
        </button>
        <video src="clips/Understanding.mp4" ref={vidRef} autoPlay />
      </div>
    </>
  );
};

export default Risk;
