import React, { useEffect, useRef } from "react";
import "./explanations.css";

const Risk = ({ onClose }) => {
  const videosList = [
    "How to gain Diversification Benefits",
    "Portfolio Diversification",
    "Sharpe Ratio",
    "Understanding Risks",
  ];

  const vidRef = useRef();

  var explanationButtons = videosList.map((expl) => (
    <button
      className="button buttonExplanation"
      key={Math.random()}
      onClick={handleClick}
      expl={capitalize(expl)}
    >
      {expl}
    </button>
  ));

  return (
    <>
      <div className="modalStyles" />
      <div className="overlayStyles">
        <button className="button buttonEndExplanation" onClick={onClose}>
          End Explanation
        </button>
        <video src="clips/Understanding.mp4" ref={vidRef} autoPlay />
      </div>
    </>
  );
};

export default Risk;
