import { useCallback, useRef, useState, useEffect } from "react";

const Recording = () => {
  const mediaRecorder = useRef(null);
  const mediaChunks = useRef([]);
  const mediaStream = useRef(null);
  const [mediaBlobUrl, setMediaBlobUrl] = useState(null);
  const audio = true;

  const getMediaStream = useCallback(async () => {
    const audioStream = await window.navigator.mediaDevices.getUserMedia({
      audio: true,
    });
    audioStream
      .getAudioTracks()
      .forEach((audioTrack) => audioStream.addTrack(audioTrack));
    mediaStream.current = audioStream;
  }, []);

  useEffect(() => {
    if (!mediaStream.current) {
      getMediaStream();
    }
  }, [audio, getMediaStream]);

  const onRecordingStop = () => {
    const [chunk] = mediaChunks.current;
    const blobProperty = Object.assign(
      { type: chunk.type },
      { type: "audio/wav" }
    );
    const blob = new Blob(mediaChunks.current, blobProperty);
    const url = URL.createObjectURL(blob);
    setMediaBlobUrl(url);
  };

  const pushChunk = ({ data }) => {
    mediaChunks.current.push(data);
  };

  const startRecording = async () => {
    if (!mediaStream.current) {
      await getMediaStream();
    }
    if (mediaStream.current) {
      const isStreamEnded = mediaStream.current
        .getTracks()
        .some((track) => track.readyState === "ended");
      if (isStreamEnded) {
        await getMediaStream();
      }

      if (!mediaStream.current.active) {
        return;
      }
      mediaRecorder.current = new MediaRecorder(mediaStream.current);
      mediaRecorder.current.ondataavailable = pushChunk;
      mediaRecorder.current.onstop = onRecordingStop;
      mediaRecorder.current.start();
    }
  };

  const stopRecording = () => {
    if (mediaRecorder.current) {
      if (mediaRecorder.current.state !== "inactive") {
        mediaRecorder.current.stop();
        mediaStream.current &&
          mediaStream.current.getTracks().forEach((track) => track.stop());
        mediaChunks.current = [];
      }
    }
  };

  return (
    <div>
      <button onClick={startRecording}>Start Recording</button>
      <button onClick={stopRecording}>Stop Recording</button>
      <video src={mediaBlobUrl} controls autoPlay loop />
    </div>
  );
};

export default Recording;
