import { useEffect, useState } from "react";
import { ReactMediaRecorder } from "react-media-recorder";

const About = () => {
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [chunks, setChunks] = useState([]);

  let playAudio = document.getElementById("adioPlay");

  var constraints = (window.constraints = {
    audio: true,
    video: false,
  });

  useEffect(() => {
    if (mediaRecorder !== null) {
      mediaRecorder.start();
      console.log("successfull!!!!!!!!!!!!!!!!!!!!!!!!");
    }
  }, [mediaRecorder]);

  function rec() {
    setChunks([]);
    navigator.mediaDevices.getUserMedia(constraints).then(function (stream) {
      setMediaRecorder(new MediaRecorder(stream));
      if (mediaRecorder != null) {
        mediaRecorder.ondataavailable = function (e) {
          console.log(e.data);
          setChunks([...chunks, e.data]);
        };
      }

      //useEffect(() => {
      //  if (mediaRecorder !== null) {
      //    mediaRecorder.start();
      //    console.log("successfull!!!!!!!!!!!!!!!!!!!!!!!!");
      //  }
      //}, [mediaRecorder]);
    });
  }

  function sleep(milliseconds) {
    const date = Date.now();
    let currentDate = null;
    do {
      currentDate = Date.now();
    } while (currentDate - date < milliseconds);
  }

  //const addChunk = (chunk) => setChunks([...chunks, chunk]);

  function stop() {
    try {
      mediaRecorder.stop();
      console.log("stopped");
      mediaRecorder.ondataavailable = function (e) {
        setChunks([...chunks, e.data]);
        console.log(chunks);
      };
      mediaRecorder.onstop = function (e) {
        console.log("Recording stopped");
        //console.log(chunks);
        let audioData = new Blob(chunks, { type: "audio/mp3;" });
        let audioUrl = window.URL.createObjectURL(audioData);
        playAudio.src = audioUrl;
        //const audio = new Audio(audioUrl);
        //audio
        //  .play()
        //  .then(function () {})
        //  .catch(function (error) {
        //    console.log(error);
        //  });
      };
    } catch (e) {
      console.log("Recording probably not started");
    }
  }

  return (
    <>
      <h1>About Page</h1>
      <button className="record" onClick={() => rec()}>
        Start
      </button>
      <button className="stop" onClick={() => stop()}>
        Stop
      </button>
      <audio id="adioPlay" controls></audio>
    </>
  );
};

export default About;
