import React, { lazy, Suspense, useState } from "react";

const ExplanationButtons = () => {
  //var explanations = ["Stocks"];
  const [explanations, setExplanations] = useState(["Stocks"]);
  const [ExplanationComponent, setExplanationComponent] = useState(null);

  const handleClick = (e) => {
    const explanationName = e.currentTarget.getAttribute("expl");
    const ExplanationComponent = lazy(() => import(`./${explanationName}`));
    setExplanationComponent(ExplanationComponent);
  };

  const handleRemove = (closedExplanation) => {
    const removedExplanation = explanations.filter(
      (explanation) => closedExplanation !== explanation
    );

    setExplanations(removedExplanation);
  };

  var explanationButtons = explanations.map((expl) => (
    <button
      className="button buttonExplanation"
      key="hi"
      onClick={handleClick}
      expl={expl}
    >
      {expl}
    </button>
  ));

  return (
    <>
      {ExplanationComponent && (
        <Suspense fallback="Loading">
          <ExplanationComponent
            onClose={() => {
              handleRemove(ExplanationComponent);
              setExplanationComponent(null);
            }}
          />
        </Suspense>
      )}
      <div>{explanationButtons}</div>
    </>
  );
};

export default ExplanationButtons;
