import { useState } from "react";
import sttFromMic from "./S2t";

const EducatorButton = () => {
  const [recording, setRecording] = useState(false);
  let [startRec, stopRec] = sttFromMic();

  switch (recording) {
    case false:
      return (
        <button
          className="button button1"
          onClick={() => {
            startRec();
            setRecording(true);
          }}
        >
          Start Educator
        </button>
      );
    case true:
      return (
        <button
          className="button button1"
          onClick={() => {
            stopRec();
            setRecording(false);
          }}
        >
          Stop Educator
        </button>
      );
  }
};

export default EducatorButton;
