import { useCallback, useRef, useState } from "react";
import S2t from "./S2t";

const Recording = () => {
  const mediaRecorder = useRef(null);
  const mediaChunks = useRef([]);
  const stream = useRef(null);
  const [mediaBlobUrl, setMediaBlobUrl] = useState(null);
  const [blob, setBlob] = useState(null);
  const [array, setArray] = useState(null);

  const getMediaStream = useCallback(async () => {
    const audioStream = await window.navigator.mediaDevices.getUserMedia({
      audio: true,
    });
    audioStream
      .getAudioTracks()
      .forEach((audioTrack) => audioStream.addTrack(audioTrack));
    // console.log(audioStream.getAudioTracks());
    stream.current = audioStream;
  }, []);

  const onRecordingStop = () => {
    const blobProperty = Object.assign(
      { type: mediaChunks.current.type },
      { type: "audio/wav" }
    );
    setBlob(new Blob(mediaChunks.current, blobProperty));
    // console.log(blob);
    const url = URL.createObjectURL(blob);
    const reader = new FileReader();
    setArray(reader.readAsArrayBuffer(blob));
    setMediaBlobUrl(url);
  };

  const pushChunk = ({ data }) => {
    mediaChunks.current.push(data);
  };

  const startRecording = async () => {
    if (!stream.current) {
      await getMediaStream();
    }
    if (stream.current) {
      const isStreamEnded = stream.current
        .getTracks()
        .some((track) => track.readyState === "ended");
      if (isStreamEnded) {
        await getMediaStream();
      }

      mediaRecorder.current = new MediaRecorder(stream.current);
      mediaRecorder.current.ondataavailable = pushChunk;
      mediaRecorder.current.onstop = onRecordingStop;
      mediaRecorder.current.start();
    }
  };

  const stopRecording = () => {
    if (mediaRecorder.current) {
      if (mediaRecorder.current.state !== "inactive") {
        mediaRecorder.current.stop();
        stream.current &&
          stream.current.getTracks().forEach((track) => track.stop());
        mediaChunks.current = [];
      }
    }
  };

  return (
    <div>
      <button onClick={startRecording}>Start Recording</button>
      <button onClick={stopRecording}>Stop Recording</button>
      {array && <S2t audio={array} />}
      <video src={mediaBlobUrl} controls autoPlay loop />
    </div>
  );
};

export default Recording;
