import React, { useState } from "react";
import "./explanations.css";
import useSound from "use-sound";
import aud1 from "./audios/stocks/Advantages and Disadvantages.mp3";
import aud2 from "./audios/stocks/Common and Preferred Stocks.mp3";
import aud3 from "./audios/stocks/How do you buy stocks.mp3";
import aud4 from "./audios/stocks/Shareholder and Equity Ownership.mp3";
import aud5 from "./audios/stocks/Understanding Stocks.mp3";

export default function Stocks({ onClose }) {
  const [hoverAudio1, setHoverAudio1] = useState(false);
  //const fs = require("fs");

  const subTopics = [
    "Advantages and Disadvantages",
    "Common and Preferred Stocks",
    "How do you buy stocks",
    "Shareholder and Equity Ownership",
    "Understanding Stocks",
  ];

  //fs.readdir("./audios/stocks", function (err, files) {
  //  //handling error
  //  if (err) {
  //    return console.log("Unable to scan directory: " + err);
  //  }
  //  //listing all files using forEach
  //  files.forEach(function (file) {
  //    // Do whatever you want to do with the file
  //    console.log(file);
  //  });
  //});

  var explanationButtons = subTopics.map((subTopic) => {
    const DynamicComponent = lazy(() =>
      import(`./audios/stocks/${subTopic}.mp3`)
    );
  });

  const [playAudio1, { stop }] = useSound(audio, {
    volume: 1.0,
  });

  //const [isPlaying, setIsPlaying] = useState(false);
  //let audio = new Audio("/audio/stocks/Understanding Stocks.mp3");

  //const start = () => {
  //  //if (!isPlaying) {
  //  //  audio.play();
  //  //  setIsPlaying(true);
  //  //} else {
  //  //  audio.pause();
  //  //  audio.currentTime = 0;
  //  //  setIsPlaying(false);
  //  //}
  //  const [play] = useSound(audio);
  //};

  const play = () => {
    if (!hoverAudio1) {
      setHoverAudio1(true);
      playAudio1();
    } else {
      setHoverAudio1(false);
      stop();
    }
  };

  return (
    <>
      <div className="modalStyles" />
      <div className="overlayStyles">
        <button onClick={play}>Play</button>
        <button
          className="button buttonEndExplanation"
          onClick={() => {
            onClose();
            stop();
          }}
        >
          End Explanation
        </button>
        Stocks
      </div>
    </>
  );
}
